mapper.select:
  	SELECT ID, NAME ,EMAIL ,PHONE ,JOINDATE , AUTH 
	FROM ZZZUSER z 
	WHERE ID = #{id}
	AND PASSWORD  = #{password}
	AND DELFLAG = 'Y'

  		SELECT COUNT(*) 
			FROM ZZZUSER z 
			WHERE ID = #{id}

mapper.update:
  		UPDATE ZZZUSER SET 
	  			PASSWORD = #{password},  
				EMAIL = #{email}, 
				PHONE = #{phone}
			WHERE ID = #{id}
